import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [user, setUser] = useState({
    name: '',
    age: '',
    profession: '',
    password: '',
    address: '',
  });
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [loginError, setLoginError] = useState(false);
  const [signupSuccess, setSignupSuccess] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSignup = async () => {
    try {
      const response = await axios.post('http://localhost:8000/info/signup', user);
      if (response.status === 201) {
        console.log('Signup successful!');
        setSignupSuccess(true);
      } else {
        console.log('Signup failed.');
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('/api/login', user);
      if (response.status === 200) {
        console.log('Login successful!');
        setLoggedInUser(user);
        setLoginError(false);
      } else {
        console.log('Login failed.');
        setLoginError(true);
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  return (
    <div className="App">
      <h1>User Authentication</h1>

      {loggedInUser ? (
        <div className="user-details">
          <p>Welcome, {loggedInUser.name}!</p>
        </div>
      ) : (
        <div>
          <h2>Login</h2>
          <input type="text" placeholder="Username" name="name" onChange={handleInputChange} />
          <input type="password" placeholder="Password" name="password" onChange={handleInputChange} />
          <button onClick={handleLogin}>Login</button>
          {loginError && <p style={{ color: 'red' }}>Login failed. Please try again.</p>}
        </div>
      )}

      {!loggedInUser && (
        <div>
          <h2>Signup</h2>
          <input type="text" placeholder="Username" name="name" onChange={handleInputChange} />
          <input type="password" placeholder="Password" name="password" onChange={handleInputChange} />
          <button onClick={handleSignup}>Signup</button>
          {signupSuccess && <p style={{ color: 'green' }}>Signup successful!</p>}
        </div>
      )}
    </div>
  );
}

export default App;
