import React, { useState } from 'react';
import './App.css'; // Importing the CSS file

const App = () => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({
    name: '',
    age: '',
    profession: '',
    address: ''
  });

  const handleChange = e => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };

  const handleSearch = () => {
    const searchedUser = users.find(user => user.name === search);
    if (searchedUser) {
      alert(`Name: ${searchedUser.name}\nAge: ${searchedUser.age}\nProfession: ${searchedUser.profession}\nAddress: ${searchedUser.address}`);
    } else {
      alert('User not found');
    }
  };

  const addUser = () => {
    setUsers([...users, form]);
    setForm({ name: '', age: '', profession: '', address: '' });
  };

  return (
    <div className="app">
      <div className="input-field">
        <input name="name" value={form.name} onChange={handleChange} placeholder="Name" />
        <input name="age" value={form.age} onChange={handleChange} placeholder="Age" />
        <input name="profession" value={form.profession} onChange={handleChange} placeholder="Profession" />
        <input name="address" value={form.address} onChange={handleChange} placeholder="Address" />
        <button onClick={addUser}>Add User</button>
      </div>

      <div className="search-field">
        <input value={search} onChange={e => setSearch(e.target.value)} placeholder="Search by name" />
        <button onClick={handleSearch}>Search</button>
      </div>
    </div>
  );
};

export default App;
