import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const [userData, setUserData] = useState({
    name: '',
    age: '',
    profession: '',
    password: '',
  });
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [searchName, setSearchName] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [signupSuccess, setSignupSuccess] = useState(false); // New state for signup success message

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSwitchForm = () => {
    setIsLogin(!isLogin);
  };

  const handleSignup = async () => {
    try {
      const response = await axios.post('http://localhost:8000/infsignup/', userData);
      console.log(response.data); // assuming backend sends back user data upon successful signup
      setSignupSuccess(true); // Set signup success message to true
      setIsLogin(true); // Switch to login form after successful signup
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8000/login/', userData);
      console.log(response.data); // assuming backend sends back user data upon successful login
      setLoggedInUser(response.data); // Store logged-in user data
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/search/?name=${searchName}`);
      console.log(response.data);
      setSearchResult(response.data);
    } catch (error) {
      console.error('Error during search:', error);
    }
  };

  return (
    <div className="App">
      <h1>User Authentication</h1>
      {isLogin ? (
        <div>
          <h2>Login</h2>
          <input type="text" placeholder="Username" name="name" onChange={handleInputChange} />
          <input type="password" placeholder="Password" name="password" onChange={handleInputChange} />
          <button onClick={handleLogin}>Login</button>
          <p>Don't have an account? <button onClick={handleSwitchForm}>Sign Up</button></p>
        </div>
      ) : (
        <div>
          <h2>Sign Up</h2>
          <input type="text" placeholder="Name" name="name" onChange={handleInputChange} />
          <input type="text" placeholder="Age" name="age" onChange={handleInputChange} />
          <input type="text" placeholder="Profession" name="profession" onChange={handleInputChange} />
          <input type="password" placeholder="Password" name="password" onChange={handleInputChange} />
          <button onClick={handleSignup}>Sign Up</button>
          {signupSuccess && <p style={{ color: 'green' }}>Signup successful!</p>} {/* Display signup success message */}
          <p>Already have an account? <button onClick={handleSwitchForm}>Login</button></p>
        </div>
      )}

      {loggedInUser && (
        <div className="user-details">
          <h2>User Details</h2>
          <p>Name: {loggedInUser.name}</p>
          <p>Age: {loggedInUser.age}</p>
          <p>Profession: {loggedInUser.profession}</p>
        </div>
      )}

      <div>
        <h2>Search User</h2>
        <input type="text" placeholder="Search by name" value={searchName} onChange={(e) => setSearchName(e.target.value)} />
        <button onClick={handleSearch}>Search</button>
        {searchResult && (
          <div>
            {searchResult === 'User not found' ? (
              <p>User not found</p>
            ) : (
              <div className="search-result">
                <h3>User Found</h3>
                <p>Name: {searchResult.name}</p>
                <p>Profession: {searchResult.profession}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
