// App.js
import React, { useState } from 'react';
import './App.css';
import axios from 'axios'; // Import Axios

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const [user, setUser] = useState({
    name: '',
    age: '',
    profession: '',
    password: '',
    address: '',
  });
  const [signupSuccess, setSignupSuccess] = useState(false);
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [loginError, setLoginError] = useState(false);
  const [jwtToken, setJwtToken] = useState(null); // Store the JWT token here
  const [searchResult, setSearchResult] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSignup = async () => {
    try {
      // Make the API request for signup
      const response = await axios.post('your_api_endpoint_here', user);

      if (response.status === 201) {
        console.log('Signup successful!');
        setSignupSuccess(true);
      } else {
        console.log('Signup failed.');
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  const handleLogin = async () => {
    try {
      // Make the API request for login
      const response = await axios.post('your_login_api_endpoint_here', user);

      if (response.status === 200) {
        console.log('Login successful!');
        setLoggedInUser(user);
        setLoginError(false);

        // Simulate token retrieval (replace with actual token from backend)
        const mockToken = 'your_jwt_token_here';
        setJwtToken(mockToken);
      } else {
        console.log('Login failed.');
        setLoginError(true);
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  const handleSearch = async () => {
    try {
      // Make the API request for search
      const response = await axios.get(`your_search_api_endpoint_here?name=${user.name}`, {
        headers: {
          Authorization: `Bearer ${jwtToken}`,
        },
      });

      if (response.status === 200) {
        setSearchResult(response.data); // Assuming the response contains user details
      } else {
        setSearchResult('User not found');
      }
    } catch (error) {
      console.error('Error during search:', error);
    }
  };

  return (
    <div className="App">
      {/* ... (other components) */}

      {loggedInUser && (
        <div className="user-details">
          <p>Name: {loggedInUser.name}</p>
          {/* ... (other user details) */}
        </div>
      )}

      {searchResult && (
        <div className="search-result">
          {searchResult === 'User not found' ? (
            <p>User not found</p>
          ) : (
            <>
              <p>Name: {searchResult.name}</p>
              <p>Age: {searchResult.age}</p>
              <p>Profession: {searchResult.profession}</p>
              <p>Address: {searchResult.address}</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
