// App.js
import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const [user, setUser] = useState({
    name: '',
    age: '',
    profession: '',
    password: '',
    address: '',
  });
  const [signupSuccess, setSignupSuccess] = useState(false);
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [loginError, setLoginError] = useState(false);
  const [searchResult, setSearchResult] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSignup = async () => {
    try {
      // Make the API request for signup
      const response = await axios.post('/api/signup', user);

      if (response.status === 201) {
        console.log('Signup successful!');
        setSignupSuccess(true);
      } else {
        console.log('Signup failed.');
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  const handleLogin = async () => {
    try {
      // Make the API request for login
      const response = await axios.post('/api/login', user);

      if (response.status === 200) {
        console.log('Login successful!');
        setLoggedInUser(user);
        setLoginError(false);
      } else {
        console.log('Login failed.');
        setLoginError(true);
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  const handleSearch = async () => {
    try {
      // Make the API request for search
      const response = await axios.get(`/api/search?name=${user.name}`);

      if (response.status === 200) {
        setSearchResult(response.data); // Assuming the response contains user details
      } else {
        setSearchResult('User not found');
      }
    } catch (error) {
      console.error('Error during search:', error);
    }
  };

  return (
    <div className="App">
      <button onClick={() => setIsLogin(!isLogin)}>
        Switch to {isLogin ? 'Signup' : 'Login'}
      </button>

      {isLogin ? (
        <div className="login-form">
          {/* ... (login form input fields) */}
          <button onClick={handleLogin}>Login</button>
          {loginError && <p style={{ color: 'red' }}>Invalid name or password</p>}
        </div>
      ) : (
        <div className="signup-form">
          {/* ... (signup form input fields) */}
          <button onClick={handleSignup}>Signup</button>
          {signupSuccess && <p>Signup is successful!</p>}
        </div>
      )}

      {loggedInUser && (
        <div className="user-details">
          <p>Name: {loggedInUser.name}</p>
          {/* ... (other user details) */}
        </div>
      )}

      {searchResult && (
        <div className="search-result">
          {searchResult === 'User not found' ? (
            <p>User not found</p>
          ) : (
            <>
              <p>Name: {searchResult.name}</p>
              <p>Age: {searchResult.age}</p>
              <p>Profession: {searchResult.profession}</p>
              <p>Address: {searchResult.address}</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
