import React, { useState } from 'react';

const App = () => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({
    name: '',
    age: '',
    profession: '',
    address: ''
  });

  const handleChange = e => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };

  const handleSearch = e => {
    setSearch(e.target.value);
  };

  const addUser = () => {
    setUsers([...users, form]);
    setForm({ name: '', age: '', profession: '', address: '' });
  };

  const searchedUser = users.find(user => user.name === search);

  return (
    <div>
      <input name="name" value={form.name} onChange={handleChange} placeholder="Name" />
      <input name="age" value={form.age} onChange={handleChange} placeholder="Age" />
      <input name="profession" value={form.profession} onChange={handleChange} placeholder="Profession" />
      <input name="address" value={form.address} onChange={handleChange} placeholder="Address" />
      <button onClick={addUser}>Add User</button>

      <input value={search} onChange={handleSearch} placeholder="Search by name" />

      {searchedUser && (
        <div>
          <h2>{searchedUser.name}</h2>
          <p>Age: {searchedUser.age}</p>
          <p>Profession: {searchedUser.profession}</p>
          <p>Address: {searchedUser.address}</p>
        </div>
      )}
    </div>
  );
};

export default App;
