// App.js
import React, { useState } from 'react';
import './App.css';

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const [user, setUser] = useState({
    name: '',
    age: '',
    profession: '',
    password: '',
    address: '',
  });
  const [signupSuccess, setSignupSuccess] = useState(false);
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [loginError, setLoginError] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSignup = () => {
    // Here you would send the data to the backend for insertion into the database
    console.log('Sending to backend:', user);
    setSignupSuccess(true);
  };

  const handleLogin = () => {
    // Here you would handle the login logic
    // For demonstration purposes, let's assume successful login
    if (user.name === 'demo' && user.password === 'password') {
      setLoggedInUser(user);
      setLoginError(false);
    } else {
      setLoginError(true);
    }
  };

  const handleSearch = () => {
    // Here you would handle the search logic
    console.log('Search logic goes here');
  };

  return (
    <div className="App">
      <button onClick={() => setIsLogin(!isLogin)}>
        Switch to {isLogin ? 'Signup' : 'Login'}
      </button>

      {isLogin ? (
        <div className="login-form">
          <input type="text" placeholder="Name" onChange={handleInputChange} name="name" />
          <input type="password" placeholder="Password" onChange={handleInputChange} name="password" />
          <button onClick={handleLogin}>Login</button>
          {loginError && <p style={{ color: 'red' }}>Invalid name or password</p>}
        </div>
      ) : (
        <div className="signup-form">
          <input type="text" placeholder="Name" onChange={handleInputChange} name="name" />
          <input type="number" placeholder="Age" onChange={handleInputChange} name="age" />
          <input type="text" placeholder="Profession" onChange={handleInputChange} name="profession" />
          <input type="text" placeholder="Address" onChange={handleInputChange} name="address" />
          <input type="password" placeholder="Password" onChange={handleInputChange} name="password" />
          <button onClick={handleSignup}>Signup</button>
          {signupSuccess && <p>Signup is successful!</p>}
        </div>
      )}

      {loggedInUser && (
        <div className="user-details">
          <p>Name: {loggedInUser.name}</p>
          <p>Age: {loggedInUser.age}</p>
          <p>Profession: {loggedInUser.profession}</p>
          <p>Address: {loggedInUser.address}</p>
        </div>
      )}

      {isLogin && (
        <div className="search-section">
          <input type="text" placeholder="Search by name" />
          <button onClick={handleSearch}>Search</button>
        </div>
      )}
    </div>
  );
}

export default App;
